function.body:
v_result NUMBER;
BEGIN
    BEGIN
        v_result = c_convert_doc_currency(
                p_amount,
                p_curfrom_id,
                p_curto_id,
                p_convdate,
                p_c_invoice_id,
                p_fin_payment_id,
                p_fin_finacc_transaction_id,
                p_client_id,
                p_org_id
            );
    EXCEPTION
        WHEN OTHERS THEN
            v_result = 0;
    END;

    RETURN v_result;
END ETBI_CONVERT_DOC_CURRENCY
